    if low < high:
      mid = (low + high) // 2
      self.merge_sort(low, mid)
      self.merge_sort(mid + 1, high)
      self.merge(low, mid, high)

  def merge(self, low, mid, high):
    b = []
    i = low
    j = mid + 1

    while i <= mid and j <= high:
      if self.values[i] <= self.values[j]:
        b.append(self.values[i])
        i += 1
      else:
        b.append(self.values[j])
        j += 1

    while i <= mid:
